import { useRouter } from 'next/router'
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import React, { useState, useRef } from 'react'
import { useScrollPosition } from '@n8tb1t/use-scroll-position'
import useDimensions from "react-cool-dimensions";

const Post = ({ post }) => {
  const router = useRouter()
  const { slug } = router.query

  const [textBodyTop, setTextBodyTop] = useState({
    top: 100
  })

  const ref = useRef();
  const { width, imagesHeight } = useDimensions({ ref });

  useScrollPosition(
    ({ prevPos, currPos }) => {
      console.log('x:' + currPos.x + ' y: ' + currPos.y)
      setTextBodyTop(currPos.y)
    },
    [textBodyTop]
  )

  console.log("Top Position")
  console.log(textBodyTop)

  return (
    <Container className="post-container" fluid>
      <Row>
        <Col className="post-body" style={{ top: textBodyTop + 30 }} sm="5" med="5" lg="5">
          <p>Height: {height}</p>
          {post.Body}
        </Col>
        <Col sm="5" med="5" lg="5">
        </Col>
        <Col ref={ref} className="post-images" sm="7" med="7" lg="7">
          <Col className="image-gallery" sm="12" med="12" lg="12">
            <Container fluid>
              <Row>
                {
                  post.Images.Image.map(image => {
                    if (image.Orientation == 'Portrait') {
                      console.log("Image Object:")
                      console.log(image)
                      return(
                        <Col className="gallery-image" sm="6" md="6" lg="6">
                          <img src={image.Image?.url} />
                        </Col>
                      )
                    } else {
                      return (
                        <Col className="gallery-image" sm="12" md="12" lg="12">
                          <img src={image.Image?.url} />
                        </Col>
                      )
                    }
                  })
                }
              </Row>
            </Container>
          </Col>
        </Col>
      </Row>
    </Container>
  )
}

export async function getServerSideProps(context)  {

  const { API_URL } = process.env

  const res = await fetch(`${API_URL}/photo-journals?Slug=${context.params.slug}`)
  const post_data = await res.json()

  return {
    props: {
      post: post_data[0]
    }
  }
}

export default Post
