import { useRouter } from 'next/router'
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import React, { useState } from 'react'
import { useScrollPosition } from '@n8tb1t/use-scroll-position'

const [headerStyle, setHeaderStyle] = useState({
  transition: 'all 200ms ease-in'
})

useScrollPosition(
  ({ prevPos, currPos }) => {
    const isVisible = currPos.y > prevPos.y

    const shouldBeStyle = {
      visibility: isVisible ? 'visible' : 'hidden',
      transition: `all 200ms ${isVisible ? 'ease-in' : 'ease-out'}`,
      transform: isVisible ? 'none' : 'translate(0, -100%)'
    }

    if (JSON.stringify(shouldBeStyle) === JSON.stringify(headerStyle)) return

    setHeaderStyle(shouldBeStyle)
  },
  [headerStyle]
)

const Post = ({ post }) => {
  const router = useRouter()
  const { slug } = router.query

  console.log(post)
  console.log("Style Object")
  console.log(headerStyle)
  return (
    <Container className="post-container" fluid>
      <Row>
        <Col className="post-body" sm="5" med="5" lg="5">
          {post.Body}
        </Col>
        <Col sm="5" med="5" lg="5">
        </Col>
        <Col className="post-images" sm="7" med="7" lg="7">
          <Col className="image-gallery" sm="12" med="12" lg="12">
            <Container fluid>
              <Row>
                {
                  post.Images.Image.map(image => {
                    if (image.Orientation == 'Portrait') {
                      console.log("Image Object:")
                      console.log(image)
                      return(
                        <Col className="gallery-image" sm="6" md="6" lg="6">
                          <img src={image.Image?.url} />
                        </Col>
                      )
                    } else {
                      return (
                        <Col className="gallery-image" sm="12" md="12" lg="12">
                          <img src={image.Image?.url} />
                        </Col>
                      )
                    }
                  })
                }
              </Row>
            </Container>
          </Col>
        </Col>
      </Row>
    </Container>
  )
}

export async function getServerSideProps(context)  {

  const { API_URL } = process.env

  const res = await fetch(`${API_URL}/photo-journals?Slug=${context.params.slug}`)
  const post_data = await res.json()

  return {
    props: {
      post: post_data[0]
    }
  }
}

export default Post
