import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import PostListing from '../components/PostListing'
import ItemCarousel from '../components/ItemCarousel'
import Footer from '../components/Footer'
import fetch from 'isomorphic-unfetch'

const Home = ({ posts, destinations }) => {

  let postCounter = 0;

  return (
    <>
      <Container className="posts-container" fluid>
        <Row>
          {
            posts.map(post => {

              if (post.Orientation == 'Portrait') {
                postCounter = postCounter + 1
              } else {
                postCounter = postCounter + 2
              }

              if (post.Destination?.parent_destination) {
                const parent_destination = destinations.filter(function id(destination) {
                  return destination.id == post.Destination.parent_destination;
                })[0]

                if (postCounter == 12) {
                  return (
                    <>
                      <PostListing post={post} parent_destination={parent_destination} />
                      <Col sm="12" md="12" lg="12" className="carousel-container">
                        <ItemCarousel />
                      </Col>
                    </>
                  )
                } else {
                  return (
                    <PostListing post={post} parent_destination={parent_destination} />
                  )
                }
              } else {
                if (postCounter == 12) {
                  return (
                    <>
                      <PostListing post={post} />
                      <Col sm="12" md="12" lg="12" className="carousel-container">
                        <ItemCarousel />
                      </Col>
                    </>
                  )
                } else {
                  return (
                    <PostListing post={post} />
                  )
                }
              }
            })
          }
        </Row>
      </Container>
      <Footer />
    </>
  )
}

export async function getServerSideProps()  {
  const { API_URL } = process.env

  const res_1 = await fetch(`${API_URL}/photo_journals`)
  const post_data = await res_1.json()

  const res_2 = await fetch(`${API_URL}/destinations`)
  const destinations_data = await res_2.json()

  return {
    props: {
      posts: post_data,
      destinations: destinations_data
    }
  }
}

export default Home
