import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Link from 'next/link';

const PostListing = ({ post, parent_destination }) => {
  if (post.Hero.Orientation == 'Portrait') {
    return (
      <Col className="post-listing" sm={6} md={4} lg={3}>
        <Link href={'post/' + post.Slug} as={`post/${post.Slug}`}>
          <a>
            <img src={post.Hero.Image.url} width="100%" />
          </a>
        </Link>
        <p className="post-tag">
          PHOTO JOURNAL {parent_destination ? '// ' + post.Destination?.Name + ', ' + parent_destination?.Name : post.Destination?.Name}
        </p>
        <p className="post-title">{post.Title}</p>
        <p className="post-blurb">{post.Tagline}</p>
      </Col>
    )
  } else {
    return (
      <Col className="post-listing" sm={12} md={8} lg={6}>
        <Link href={'post/' + post.Slug} as={`post/${post.Slug}`}>
          <a>
            <img src={post.Hero.Image.url} width="100%" />
          </a>
        </Link>
        <p className="post-tag">
          PHOTO JOURNAL {parent_destination ? '// ' + post.Destination?.Name + ', ' + parent_destination?.Name : post.Destination?.Name}
        </p>
        <p className="post-title">{post.Title}</p>
        <p className="post-blurb">{post.Tagline}</p>
      </Col>
    )
  }
}

export default PostListing
