import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import PostListing from '../components/PostListing'
import fetch from 'isomorphic-unfetch'

const Home = ({ posts }) => {
  console.log(posts)
  return (
    <Container className="posts-container" fluid>
      <Row>
        {
          posts.map(post => {
            if (post.Hero.Orientation == 'Portrait') {
              return (
                <Col className="post-listing" sm={6} md={4} lg={3}>
                  <img src={post.Hero.Image.url} width="100%" />
                  <p class="post-title">{post.Title}</p>
                  <p class="post-blurb">{post.Tagline}</p>
                </Col>
              )
            } else {
              return (
                <Col className="post-listing" sm={12} md={8} lg={6}>
                  <img src={post.Hero.Image.url} width="100%" />
                  <p class="post-title">{post.Title}</p>
                  <p class="post-blurb">{post.Tagline}</p>
                </Col>
              )
            }
          })
        }
      </Row>
    </Container>
  )
}

export async function getServerSideProps()  {
  const { API_URL } = process.env

  const res = await fetch(`${API_URL}/photo-journals`)
  const data = await res.json()

  return {
    props: {
      posts: data
    }
  }
}

export default Home
