import { useRouter } from 'next/router'
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import React, { useState, useRef } from 'react'
import ImageGallery from 'components/ImageGallery'
import { useScrollPosition } from '@n8tb1t/use-scroll-position'

const Post = ({ post }) => {
  const router = useRouter()
  const { slug } = router.query

  const [textBodyTop, setTextBodyTop] = useState({
    top: 100
  })

  useScrollPosition(
    ({ prevPos, currPos }) => {
      console.log('x:' + currPos.x + ' y: ' + currPos.y)
      setTextBodyTop(currPos.y)
    },
    [textBodyTop]
  )

  console.log("Top Position")
  console.log(textBodyTop)

  return (
    <Container className="post-container" fluid>
      <Row>
        <Col className="post-body" style={{ top: textBodyTop + 30 }} sm="5" med="5" lg="5">
          <p>Height: </p>
          {post.Body}
        </Col>
        <Col sm="5" med="5" lg="5">
        </Col>
        <ImageGallery post={post} />
      </Row>
    </Container>
  )
}

export async function getServerSideProps(context)  {

  const { API_URL } = process.env

  const res = await fetch(`${API_URL}/photo-journals?Slug=${context.params.slug}`)
  const post_data = await res.json()

  return {
    props: {
      post: post_data[0]
    }
  }
}

export default Post
